INSERT INTO interviewee_tb (interviewee_id, kor_nm, eng_nm, interviewee_desc, interviewee_corp, interviewee_position, link_cd, link_url, rpsnt_image_url, create_id, create_dt, update_id, update_dt, state, city_cd, interviewee_desc_eng, interviewee_corp_eng, interviewee_position_eng) VALUES ('INTERVIEWEE000000120', '강보식', 'Kang Bosik', '2003년, 30대 중반 느닷없이 맞닥뜨린 큰아들의 소아급성백혈병을 통해 삶과 죽음 그리고 그 사이 를 직면하는 계기를 만났다. 그 이후 건강하고 행복한 삶의 의미를 찾아, 다니던 회사를 퇴직하고 숱한 시행착오 끝에 제주도로 정착하게 된지 10년이 훌쩍 넘었다. 30~40대를 통틀어 진행된 힘겨운 시행착오의 과정들이 세상에서의 관점에서 보면 일탈과 방황일 수 있겠지만, 그로인해 자신만의 꿈과 그 꿈을 이루어갈 수 있는 몸과 마음의 균형과 건강을 얻었다. 제주도라는 아름다운 자연에서의 혼자만의 시공간을 통해 자기자신을 깊게 만나고 사랑하고 하나 되는 성장의 세월을 경험으로 삼아, 자기만의 건강하고 행복한 삶의 가치를 추구하고 만들어가고자 하는 많은 사람들과 공유하기 위해, 여행사 ’더힐링아일랜드‘를 설립하고 현재 섬도보여행가이자 여행작가로서의 인생2막을 보내고 있다. 

더힐링아일랜드는 <제주휴식여행>, <제주힐링트레킹>, <제주명상캠프>, <제주다이어트캠프> 등 여행을 통한 새로운 라이프스타일의 추구와 변화를 목적으로 제주도를 중심으로 국내외 다양한 리트릿여행프로그램을 기획/운영하고 있다. ', '더힐링아일랜드', '대표', 'S0101', 'https://www.facebook.com/bskang67', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/ad500cb7-9bc1-4ef2-9cc6-b3289d5793d5.jpg', 'Huple003', STR_TO_DATE('2020-09-29 09:10:40.000', '%Y-%m-%d %H:%i:%s.%f'), 'Huple003', STR_TO_DATE('2020-09-29 09:23:04.000', '%Y-%m-%d %H:%i:%s.%f'), 'S', 'JJ', '2003년, 30대 중반 느닷없이 맞닥뜨린 큰아들의 소아급성백혈병을 통해 삶과 죽음 그리고 그 사이 를 직면하는 계기를 만났다. 그 이후 건강하고 행복한 삶의 의미를 찾아, 다니던 회사를 퇴직하고 숱한 시행착오 끝에 제주도로 정착하게 된지 10년이 훌쩍 넘었다. 30~40대를 통틀어 진행된 힘겨운 시행착오의 과정들이 세상에서의 관점에서 보면 일탈과 방황일 수 있겠지만, 그로인해 자신만의 꿈과 그 꿈을 이루어갈 수 있는 몸과 마음의 균형과 건강을 얻었다. 제주도라는 아름다운 자연에서의 혼자만의 시공간을 통해 자기자신을 깊게 만나고 사랑하고 하나 되는 성장의 세월을 경험으로 삼아, 자기만의 건강하고 행복한 삶의 가치를 추구하고 만들어가고자 하는 많은 사람들과 공유하기 위해, 여행사 ’더힐링아일랜드‘를 설립하고 현재 섬도보여행가이자 여행작가로서의 인생2막을 보내고 있다. 

