INSERT INTO interviewee_tb (interviewee_id, kor_nm, eng_nm, interviewee_desc, interviewee_corp, interviewee_position, link_cd, link_url, rpsnt_image_url, create_id, create_dt, update_id, update_dt, state, city_cd, interviewee_desc_eng, interviewee_corp_eng, interviewee_position_eng) VALUES ('INTERVIEWEE000000118', '유천규, 안경희', 'Yu Cheongyu, Ahn Gyeonghui', '“낯선 곳을 걸으며 보이는 풍경과 설레는 마음이 새로운데, 걸어온 길을 뒤돌아보면 좀 전과는 사뭇 다른 모습으로 다가오는 것이 새로워요. 언젠가 가봤던 곳을 다시 여행한다면 눈에 보이는 풍경은 비슷하겠지만 마음가짐이 새롭기 때문에 여행은 새로운 마음의 길을 걷는 것이라 생각해요.” 

물레 위에서 중심을 잡아 도자기를 만든다는 것은 마음의 중심을 잡는 것과 같은 일이라고 말하는 유천규, 안경희 부부. 그들은 한적한 시골마을인 하가리에서 흙이 주는 편안함과 즐거움을 누리며 ‘토예랑 도자기 공방’을 운영중이다.', '토예랑도자기공방', '부부', 'S0102', 'https://www.instagram.com/toyerangdoye/', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/bb4c1b95-be8b-46a6-ab2d-6145106c4917.jpg', 'Huple003', STR_TO_DATE('2020-09-10 15:11:25.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.lch', STR_TO_DATE('2021-06-28 13:58:18.000', '%Y-%m-%d %H:%i:%s.%f'), 'S', 'JJ', '“낯선 곳을 걸으며 보이는 풍경과 설레는 마음이 새로운데, 걸어온 길을 뒤돌아보면 좀 전과는 사뭇 다른 모습으로 다가오는 것이 새로워요. 언젠가 가봤던 곳을 다시 여행한다면 눈에 보이는 풍경은 비슷하겠지만 마음가짐이 새롭기 때문에 여행은 새로운 마음의 길을 걷는 것이라 생각해요.” 

