INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '노을을 기다린다는 것은 언제나 설레는 일이다. 낮 동안 푸르던 하늘이 때로는 분홍빛으로, 때로는 보랏빛으로 물들다 마침내는 붉은색으로 타오르는 모습은 하루 중 가장 아름답다고 정의하기에 손색없기 때문이다. ‘귀덕1리 마을’을 찾았던 날도 마찬가지였다. 해안선을 따라 펼쳐지는 황홀한 일몰을 마주할 생각에 가슴은 무척이나 두근거렸다. 그리고 그 여정 속에서 설레는 마음이 다소 앞서 걸음이 빨랐던 내게 따뜻한 품을 내어주던 카페, ‘말로나’와도 우연히 만나게 됐다.', '', 1, '', '');
