INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '한림읍과 애월읍으로 이어지는 해안도로를 지나자 길가에 우뚝 솟은 귀덕 궤물동산과 마주할 수 있었다. 이곳을 시작으로 귀덕1리 방파제까지 해안과 맞닿아 있는 귀덕1리의 마을 풍경이 펼쳐지고 있었는데, 나는 아직 한 시간이 넘게 남은 일몰을 기다리며 귀덕1리 마을을 천천히 걸어보고자 했다.', '', 5, '', '');
