INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '마음이 너무 앞섰던 탓일까. 귀덕리 앞바다의 상징이라고 할 수 있는 거북등대를 지나 애월읍과의 경계에 다다랐음에도 해는 아직 수평선과 거리가 꽤 멀었다. 바로 그때, 우두커니 서서 잠시 시간을 보낼 곳을 찾아 두리번거리던 내게 너르면서도 정갈한 정원과 모던한 외관이 인상적인 카페 ‘말로나’가 반가운 손짓을 건네왔다.', '', 8, '', '');
