INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '일몰이 가까워지자 신비로운 느낌을 자아내는 빛이 하늘 위에 빼곡하게 들어찬 구름 사이를 뚫고 거북등대와 바다 위로 내리기 시작했다. 시작점이었던 귀덕 궤물동산에 다시금 가까워질수록 하늘은 점차 보랏빛과 주황, 분홍빛이 뒤섞여 황홀한 분위기를 만들어냈다.', '', 23, '', '');
