INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '해의 발자취를 따라 걸으며 마주한 귀덕1리의 일몰은 소소하면서도 인상적이었다. 한적한 해안가와 해안도로 위로 내려앉는 빛은 마치 아름다운 벽화를 그리듯 평범한 해안마을 풍경에 색을 더하는 역할을 했다. 그 덕분에 귀덕1리는 내 기억 속에 아름다운 마을로 자리했다.', '', 26, '', '');
