INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000002', 3, '돌아보면 조급했던 걸음이 말로나라는 벗과 만나는 기회를 안겨주었다고 생각한다. 성급함 또는 조급함으로 표현됐던 내 걸음은 말로나를 통해 ‘부지런함’으로 적힐 수 있게 됐다. 아늑한 분위기 속 은은하게 코끝을 간지럽히던 핸드드립 커피향, 그 속에서 노을을 차분히 기다리던 순간은 한동안 쉽사리 잊히지 않을 것 같다.', '', 28, '', '');
