INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000003', 3, '여행자의 감성을 자극하는 비행기 로고에 이끌려 들어선 더 아일랜더는 제주에서의 여행을 추억할 수 있는 다양한 기념품을 판매하는 곳이었다. 그뿐만 아니라 국내외에서 엄선된 디자인 생활소품은 물론 편안한 분위기의 공간 속에서 커피를 즐길 수 있는 공간까지 알차게 마련되어 있었다.', 'I was drawn by the airplane logo that touched my emotions as a traveler. The Islander was a shop that sells souvenirs to remember and cherish what happened in Jeju. In addition, there were carefully selected collections of designed household items from domestic and abroad, as well as a café where you can enjoy a cup of coffee in the comfort of the store.', 12, '', '');
INSERT INTO local_brand_post_content_tb (post_id, contents_type, contents, contents_eng, seq, start_date, end_date) VALUES ('LPOST000000000000003', 1, 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/3c6f90ee-c5d0-4771-9549-7f2d88d8af05.jpg', '', 13, '', '');
