INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000824', '천왕사', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/76b9847b-c079-4872-83c7-a7054c3ee1e8.jpg', '어승생오름 동쪽에 아흔아홉골 속에 있는 사찰.', '건립된 지 오래되지 않았음에도 불구하고 1994년 4월에 전통 사찰로 지정되었다. 곧게 뻗은 바위들과 울창한 숲이 절경이며, 사찰 옆의 계곡을 따라 가면 한라산의 유일한 폭포라 알려진 선녀폭포가 있고, 사찰 입구에는 한라산의 노루가 마신다는 약수터도 있다. 특히 가을철 단풍으로 장관을 이루는 곳이다.', 5621, 'A0201', 'A020101', '제주시', '노형동', '1100로 2528-111', 33.4077486, 126.49501240000002, 60, 0.0, 0.0, 883, 0, 0, 2, 4, 'S', 'Huple001', STR_TO_DATE('2020-10-22 22:30:58.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.koo', STR_TO_DATE('2022-10-11 20:41:53.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011066000', 52, 38, 0, NULL, '1', NULL, NULL, 'JJ', 'Cheonwangsa Temple', '20-17 Nohyeong-dong, Cheju, Jeju-do, South Korea', 'It is a temple located in the Aheunahopgol Valley to the east of Eoseungsaeng Oreum.  ', 'Although it has been built quite recently, it was designated as a traditional temple in April 1994. The upright rocks and dense forestry are scenic, and along the valley next to the temple is Seonnyeo Falls, known as the only waterfall in Mount Halla. At the entrance of the temple is a mineral spring where the deer of Hallasan drink. It is particularly known for its spectacular autumn foliage. ', 'Jeju City', 'Noh Hyung-dong', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000823', '산방굴사', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/edb59574-1f4f-439c-91c3-24b974f9ecd6.jpg', '절벽으로 이루어진 산방산 중턱에 있는 절.', '산방산에 위치한 산방굴사는 천연석굴로 원래는 산방굴이라 불리었지만, 안에 불상을 안치해 놓아 현재는 산방굴사라 불린다. 이 곳에서 내려다 보는 절경은 영주십이경의 하나로 꼽힐 정도로 아름답다. ', 5621, 'A0202', 'A020203', '서귀포시', '안덕면', '사계리 산16', 33.23655, 126.312464, 30, 0.0, 0.0, 627, 0, 1, 4, 6, 'S', 'Huple001', STR_TO_DATE('2020-10-22 22:30:11.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 11:40:57.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013031000', 49, 32, 0, NULL, '1', NULL, NULL, 'JJ', 'Sanbanggulsa Temple', 'San 16, Sagye-ri, Andeok-myeon, Seogwipo-si, Jeju-do, Republic of Korea', 'It is a temple in the middle of Mount Sanbang made up of cliffs.', 'Sanbanggulsa located in Sanbangsan is a natural stone cave that was originally called Sanbang Cave but is now called Sanbanggulsa after enshrining inside a statue of Buddha. Due to its exquisite view, it is listed as one of the Twelve Sceneries of Yeongju. ', 'Seogwipo City', 'Andeok-myeon', NULL, NULL, NULL, NULL, NULL);
