INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000029', '부대악(부대오름)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/62b9e2fe-bd87-4f2e-aee2-22941615e9d4.jpg', '동북쪽으로 크게 벌어진 전형적인 U자형의 말굽형 화구를 가진 오름으로, 화구의 바깥사면은 삼나무와 해송이, 안쪽으로는 낙엽수림, 정상에는 생달나무와 동백나무 등의 상록수가 우거져 있다.', '숲이 우거져있어 정상에서의 전망은 아쉬운 곳으로, 간간히 나있는 틈으로 인근의 오름등을 조망할 수 있고 특히 세계자연유산에 등재된 거문오름을 볼 수 있으며 부대악과 나란히 이어져있는 부소악과 탐방 코스를 연계할 수 있다. 부소악 외에도 거문오름, 민오름, 등도 함께할 수 있다.', 5621, 'A0201', 'A020105', '제주시', '조천읍', '선흘리 산103-1번지', 33.446418, 126.70852, 60, 0.0, 0.0, 20, 0, 0, 0, 0, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-11 17:24:52.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025900', 55, 39, 0, NULL, NULL, '1', NULL, 'JJ', 'Budaeak Volcanic Cone (Budae Oreum)', 'San 103-1, Seonheul-ri, Jocheon-eup, Jeju-si, Jeju-do, Republic of Korea', '', '', 'Jeju City', 'Jocheon-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000028', '별도봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/ffaa50cd-b613-49a5-b219-086afffa0c98.jpg', '베리오름이라고도 하는 별도봉은 일출과 여명을 감상하기에 안성맞춤인 곳이다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000027', '바리메', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/08bbce35-5186-495a-9281-d75d6d0459b7.jpg', '높이 763.4m의 바리메 오름은 그 모양이 바리를 닮았다고 해 붙은 이름이다.', '바리란 절에서 여성들의 밥그릇으로 사용된 놋쇠그릇을 말한다. 정상에 바리모양을 한 분화구가 있으며 비가 많이 내릴 경우 일시적으로나마 물이 고이기도 한다. 높기 때문에 오르기가 아주 쉽지는 않지만 인기가 있고 주변에 한대오름, 노로오름 등의 오름이 있어 찾는이가 더욱 많아지고 있다.', 5621, 'A0201', 'A020104', '제주시', '애월읍', '어름리 산1번지', 33.3787099, 126.3879639, 70, 0.0, 0.0, 1271, 0, 0, 0, 2, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:33:16.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025300', 49, 37, 0, NULL, NULL, NULL, '1', 'JJ', 'Barime', 'South Korea, Jeju-do, Cheju, Aewol-eup, Sangga-ri, San 124', 'Barime Oreum, 763.4 meters high, is named after its shape that resembles a bari. ', 'Bari refers to the rice bowl for women used in temples. There is a bari-shaped crater at the top, and if it rains heavily, water sometimes pools temporarily. It is not an easy climb due to its height, but is popular, nonetheless. There are many nearby oreums, such as Handae Oreum and Noro Oreum, so the number of visitors is growing.', 'Jeju City', 'Aewol-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000026', '바농오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/2f3b01f7-41ce-4b42-858f-8001c09fd70b.jpg', '바농은 바늘을 뜻하는 제주 방언으로, 오름 주변과 능선에 가시나무가 많아서 바농오름이라 불렸으나 과거에 붙여진 이름답게 현재 가시덤불이 많다는 것을 느끼기는 어렵다.', '원형과 말굽형 두 개를 동시에 지닌 쌍둥이형 복합 화산체로 산체가 크고 넓어서 탐방의 묘미를 느낄 수 있다. 오름의 정상에서 한라산쪽으로 일직선상에 배열된 기생화산군(오름)을 눈여겨 볼 필요가 있는데, 한라산을 정점으로 하여 동북쪽 화산 산록에서는 뚜렷한 화산 구조선을 관찰할 수 있다. 인접한 오름은 없으나 접근성과 이동성은 무난하여 이곳을 중심으로 탐방 코스를 연계할 수 있다.', 5621, 'A0201', 'A020105', '제주시', '조천읍', '교래리 산108번지일대', 33.45377130000003, 126.65380300000001, 70, 0.0, 0.0, 1076, 0, 0, 10, 10, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:29:48.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025900', 55, 39, 0, '1', NULL, NULL, NULL, 'JJ', 'Banong Oreum', 'San108 Gyorae-ri, Jochon-eup, Cheju, Jeju-do, South Korea', 'Banong in Jeju dialect means needle. The name had been given because it was surrounded by thorn bushes in the past, but it is now difficult to see as many. ', 'It is a twin-shaped complex volcano in two shapes, circular and horseshoe, and the mountain body is large and wide, giving you the excitement of an expedition. It is worth examining the group of parasitic volcanoes (oreums) arranged in a straight line from the summit towards Hallasan, and a distinct line of volcanic structure can be observed at the foot of the northeast volcano with Hallasan as its peak. There are no adjacent oreums, but it has easy accessibility and mobility, allowing you to connect the adjacent exploration courses.', 'Jeju City', 'Jocheon-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000025', '민오름(무녜오름)', '', '오름의 형세가 세모꼴을 하고 있는데 제주 무당들이 쓰는 고깔인 ''송낙''과 비슷하다고 하여 무녜오름으로 불리기도 한다.', '제주시 오라동, 동부권 송당, 조천읍 선흘리 등에 있는 민오름과 같은 맥락으로 나무가 없는 민둥산의 형태라 붙여진 이름이지만 현재는 여느 오름들 처럼 나무가 울창하게 자라고 있다. 봉개동 주민자치위원회에서 민오름을 중심으로하는 산책과 탐방코스를 만드는 등 도보 여행지와 연계가 되어 접근성이 좋고 오름을 탐방하는데 있어서 코스를 선택하는 폭이 넓어졌다.', 5621, 'A0201', 'A020101', '제주시', '봉개동', '산64번지일대', 33.437254, 126.6370792, 70, 0.0, 0.0, 22, 0, 0, 0, 0, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:25:21.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011062000', 54, 38, 0, NULL, NULL, '1', NULL, 'JJ', 'Min Oreum (Munye Oreum)', '64 Bonggae-dong, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Bonggae-dong', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000024', '물찻오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/4d015823-d3e7-40b2-ab8a-3c74a5aab6be.jpg', '1년 중 사려니숲 에코힐링체험 기간에만 출입 가능한 오름이다.', '사려니숲길 붉은오름방면 입구를 기준으로 약 1시간 30분을 걸어 들어가면 만날 수 있는 물찻오름은 굼부리에 물이 차 있어 이름이 지어졌다. 제주 내 기생화산 중 몇 안되는 산정화구호에는 물이 항상 있으며 언덕 사이에는 커다란 화산탄들이 널려있다. 행사기간 외에는 보존, 복원을 이유로 출입을 통제하고 있다. ', 5621, 'A0201', 'A020105', '제주시', '조천읍', '교래리 산137-1번지일대', 33.394847900000016, 126.65287420000001, 60, 0.0, 0.0, 875, 0, 0, 1, 3, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:27:08.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025900', 55, 39, 0, NULL, '1', NULL, NULL, 'JJ', 'Mulchat Oreum', 'San 137-1, Gyorae-ri, Jocheon-eup, Jeju-si, Jeju-do, Republic of Korea', 'This oreum is accessible only during the annual Saryeoni Forest Eco-Healing Experience.', 'You can meet Mulchat Oreum, named after its water-filled crater, by walking for about an hour and a half from the entrance of Bulgeun Oreum on the Saryeoni Forest Trail. It is one of the few parasitic volcanoes in Jeju with water at the crater lake stagnant at all times, and there are large volcanic bombs scattered among the hills. At times other than the event, access is restricted for preservation and restoration reasons. ', 'Jeju City', 'Jocheon-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000023', '수산봉(물메)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/6317bd8f-c63e-4df6-95db-060d684f6334.jpg', '올레 16코스의 경유지에 포함된 오름으로, 사찰과 호수의 풍경 등 환경의 변화가 있어서 오름을 오르내리고 주변을 연계하여 산책하기 좋다.', '오래전부터 물미나 물메로 불러왔으며 수산봉으로 더 많이 알려져 있는데, 조선시대에 봉수를 설치하였던 것과 관련하여 물메봉수(水山峰)라고 하였다. 봉수는 터만 남아있으며 정상부 아래로는 체육시설과 정자가 만들어져 있고 나무와 풀이 자라는 등 변화가 이루어져 전망의 여건이 안된다.  ', 5621, 'A0201', 'A020104', '제주시', '애월읍', '수산리 산1-1번지', 33.473013200000004, 126.38455449999998, 60, 0.0, 0.0, 356, 0, 0, 3, 4, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:25:02.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025300', 49, 37, 0, NULL, NULL, '1', NULL, 'JJ', 'Susanbong Peak (Mulmae)', '1-11 Susan-ri, Aewol-eup, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Aewol-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000022', '문도지오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/555e8591-cdb7-43d8-a113-1258d6b6f1bb.jpg', '제주올레 14-1코스 중간 즈음에 있는 문도지 오름은 일반인들은 잘 모르지만 사진작가들 사이에서 출사지로 널리 알려져있다.', '일몰이 아름다우며 웨딩스냅 촬영을 하기에도 좋다. 오르는 길에 붉은 흙과 초록빛깔 풀밭에서 노니는 말을 볼 수 있다. 정상까지는 10분도 채 걸리지 않으며, 뒷편으로 한라산을 조망할 수 있고 신창풍차, 당산봉, 산방산 등 조망할 거리가 많다. 한편 문도지오름이라는 이름은 돼지에서 따온 것이다. 문돗지라고도 하는데 제주 말로 ''돗''이 돼지를 의미하기 때문이다. 다른 해석으로 문의 입구, 길목 등을 의미하는 道(도)에 地(지)가 붙었다는 의견도 있다.', 5621, 'A0201', 'A020103', '제주시', '한림읍', '금악리 3444번지', 33.3262503, 126.2946128, 40, 0.0, 0.0, 1286, 0, 0, 0, 0, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.koo', STR_TO_DATE('2022-10-11 20:36:32.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025000', 48, 36, 0, '1', NULL, '1', NULL, 'JJ', 'Mundoji Oreum', '3443 Geumak-ri, Hallim-eub, Jeju-si', 'Mundoji Oreum is located halfway through the Jeju Olle 14-1 course. Though unfamiliar to the public, it is widely known among photographers as a famous shooting spot. ', 'Its sunset is beautiful, and it is also a wonderful place for wedding snaps. On the way up, you can see horses playing on red soil and green grass. It takes less than 10 minutes to reach the summit, and you can see Mount Halla Mountain from afar. There are plenty of sights to see, such as the Shinchang Windmill, Dangsanbong, Mount Sanbang, etc.', 'Jeju City', 'Hallim Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000021', '묘산봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/32e34be0-cc31-4349-b87b-ae86a929c3f9.jpg', '''묘산봉''이라는 이름은 오름에 고양이가 살았었고 고양이가 누워있는 모습과 모양새가 비슷하다고 하여 붙여졌다.', '제주동부 제주시 구좌읍 서김녕리 369번지에 위치한 높이 116.3m의 오름. 남향의 말굽형 화산체이며 굼부리 안은 대부분 농경지로 변했다. 김녕리 주민들은 묘산봉을 ''고살미''라고도 한다. 시멘트로 된 포장길이 있어 어렵지 않게 오를 수 있는 편이다. 남쪽 기슭에는 수호신인 천신, 지신에게 마을의 무병과 안녕을 기원하는 제사를 지낸 제단의 흔적이 남아있다. ', 5621, 'A0201', 'A020106', '제주시', '구좌읍', '김녕리 2723번지', 33.5432317, 126.7452464, 50, 0.0, 0.0, 643, 0, 0, 2, 2, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.lch', STR_TO_DATE('2022-08-04 14:58:35.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025600', 59, 38, 0, NULL, NULL, '1', NULL, 'JJ', 'Myosanbong Peak', '2725 Gimnyeong-ri, Gujwa-eup, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Gujwa Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000020', '말찻오름', '', '물찻오름과 이웃한 오름이다. 말찻오름이라는 이름은 말(馬)에 찻이 붙었다는 설과 하나의 터전이나 영역을 일컫는 것이라는 설이 있다. ', '이름처럼 말들을 방목하기 좋은 곳이 있다. 조선시대에는 이곳에서 말을 길렀으며 현재도 경주마를 기를 목적으로 울타리가 쳐진 곳을 볼 수 있다. 휴양림과 함께 연계해서 둘러볼 수있으며 힐링코스로 추천할만하다. 정상에는 해맞이를 하기 좋은 전망대와 특이한 모양을 한 돌들을 볼 수 있다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000019', '둔지오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/c56a9335-b443-4ab3-beaf-840e6141e6cf.jpg', '둔지는 제주말로 평지보다 조금 높은 곳, 언덕 등을 일컫는 말이다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000018', '돝오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/5f103a26-02c7-4791-bc80-528bc8f8957b.jpg', '정상에 오르면 전형적인 원형의 굼부리를 볼 수 있으며, 둘레를 따라 산책로가 조성되어 있다. 인근에 위치한 다랑쉬오름에 다소 가려진 오름이지만 자연적인 환경이나 전망 등 입지가 좋은 오름이다.', '산체의 모양이 돼지를 닮았다고 하여 돝오름이라는 명칭이 붙었고, 비저오름이라고도 불리는데 바로 앞에 비자나무 군락지인 비자림이 있어서 붙은 명칭이다. 돝오름에 오르면 막힌 곳 없이 주변을 둘러싼 수많은 오름의 풍경을 볼 수 있으며, 비자나무들이 우거진 길목과 활엽수나 낙엽송들이 늘어선 사이를 지날 수 있고, 삼나무 숲과 억새 등으로 환경 변화가 이루어져 오름 둘레길과 정상으로 가는 코스를 갖춘 다른 오름들 보다 특별하다.', 5621, 'A0201', 'A020106', '제주시', '구좌읍', '송당리 산3번지,평대리 산16번지', 33.4810798, 126.80150679999997, 60, 0.0, 0.0, 679, 0, 0, 5, 4, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 12:46:38.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025600', 59, 38, 0, '1', NULL, NULL, NULL, 'JJ', 'Dot Oreum', 'San 3, Songdang-ri, Gujwa-eup, Jeju-si, Jeju-do, Republic of Korea', '', '', 'Jeju City', 'Gujwa Township', NULL, NULL, NULL, NULL, NULL);
