INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000016', '당오름(구좌)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/97e6c0a6-d02b-4ce1-91b7-0921c3a84b70.jpg', '제주권 본당향의 원조격 본당향이 있는 오름이라 하여 당(堂)오름이라고 한다.  ', ' 나즈막한 동산에 가까우며 숲으로 우거져 있고, 비교적 완만한 사면을 이루고 있으며 북서쪽으로 침식된 말굽형 화구를 지닌 화산체이다. 주변에 유명한 오름들이 많아서 오름이라고 하기에는 무색하지만 과거부터 마을의 오름에 신당을 짓는 관례에 따라 북서쪽 기슭의 당내라는 냇가에 당이 있어서 당오름이라는 명칭이 붙었다. 제주도 민속자료와 제주시 숨은 비경31곳에 지정된 송당본당향을 둘러볼 수 있다.
