INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000047', '원당봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/6108dc18-e29e-4412-89de-f08d957caaa0.jpg', '높이 170.7m에 7개의 봉우리로 구성된 원당봉은 원당칠봉이라고도 한다. ', '원당봉이라는 이름은 원나라때 오름 중턱에 원당의 당인 원당(元堂)이 세워져 붙었다고도 하고, 조선시대에 원당봉수가 세워졌던 적이 있어 망오름이라고 하기도 한다. 이곳에서는 원나라시대부터 조선시대에 이르기까지 봉수대의 역할을 한 역사와 더불어 사찰 탐방을 할 수 있다. 3개의 사찰이 있고 각각 조계종의 불탑사, 태고종의 원당사, 천태종의 문강사이다. 불탑사에서는 제주에 있는 유일한 불탑이자, 보물 1187호인 원당사지 5층 석탑을 볼 수 있다. 
