INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000045', '용눈이오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/0101dbc3-9a3d-4e60-b4a7-bd32dbc809f9.jpg', '화구의 모습이 마치 용이 머물렀던 자리와 같다 하여 이름붙여진 용눈이 오름은 그 인기가 대단하다.', '용이 누운 자리, 용이 놀았던 자리, 용의 눈과 같다 하여 용와악(龍臥岳), 용유악(龍遊岳), 용안악(龍眼岳)으로도 표기한다. 제주에 있는 약 360여개의 오름 중 유일하게 분화구가 3개나 있다. 완만한 경사로 인해 누구나 어렵지 않게 오를 수 있으며 정상에서 날씨가 좋을때는 멀리 성산일출봉과 우도까지 볼 수 있다. 이른 시간에 용눈이 오름을 방문하면 색다른 풍경과 함께 하루를 시작할 수 있다.', 5621, 'A0201', 'A020106', '제주시', '구좌읍', '종달리 산38번지', 33.45987990000002, 126.83268409999998, 60, 1.0, 0.0, 1218, 0, 0, 69, 101, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:29:57.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025600', 59, 38, 0, NULL, NULL, '1', '1', 'JJ', 'Yongnuni Oreum', 'Yongnuni Oreum,San39 Jongdal-ri, Gujwa-eup, Jeju', '', '', 'Jeju City', 'Gujwa Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000044', '어도오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/7fdd1833-967b-4a4e-a394-4c520da290ca.jpg', '어도오름이 위치해있는 봉성리는 과거 어도리라 불리는 마을로, 4.3이후 마을 이름을 봉성리로 개명을 하였으나 오름의 명칭은 그대로 남아 있다.', '동서 두 개의 봉우리로 이루어져 있는데 동쪽 봉우리가 정상이고 이곳에 조선시대에 봉수대가 설치됐던 흔적이 남아있으며, 서쪽 봉우리 중턱에는 도림사라는 절이 있다. 북쪽으로 벌어진 말굽형 화구를 이루고 있는 어도오름은 과거에 비해 잡목이 빽빽하게 우거져 탐방이 어렵지만 전반적으로 심한 경사가 없으며 산책로 조성이 잘 되어있다. ', 5621, 'A0201', 'A020104', '제주시', '애월읍', '봉성리 3920-24번지일대', 33.42055610000001, 126.308466, 50, 0.0, 0.0, 23, 0, 0, 1, 0, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:29:44.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025300', 49, 37, 0, '1', NULL, NULL, NULL, 'JJ', 'Eodo Oreum', '3920-35 Bongseong-ri, Aewol-eup, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Aewol-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000043', '안세미오름', '', '오름 자락에 있는 샘을 중심으로 안쪽을 안세미오름, 바깥쪽을 밧세미오름이라고 한다. 이 두 오름을 형제오름 또는 형제봉이라고도 한다.', '안세미오름은 조선시대 유학자 김진용, 호 明道庵(명도암: 길을 밝힌 사람)이 스승인 이익을 그리며 자주 오른 곳으로도 알려져있다. 이는 명도암 유허비와 안내문을 통해 확인할 수 있다. 뿐만 아니라 유배온 선비들이 다녔던 곳이기도 하다. 오름 초입에 샘이 있는데 이는 조리샘에서부터 내려온 물이다. 이 물을 총 4단계로 나눠 사용했는데 가장 높은 곳에서 낮은 곳 순으로 먹는용도, 쌀 등을 씻는 물, 설거지물, 가축이 마실 물이다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000042', '아부오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/6dd79dcf-774f-46c3-949f-3d5678c951f0.jpg', '높이 300m로 완만한 경사를 이루고 있는 아부오름은 앞오름이라고 하기도 한다. ', '이름의 유래는 여러가지가 있으나 형상이 어른이 자리잡고 앉아있는 모습과 비슷하다하여 붙었다는 설이 있다. 제주민란을 다룬 영화 ''이재수의 난''촬영지이며 인공적으로 조성한 삼나무 군락이 특징이다. 규모가 특별히 크지는 않지만 자연과 인공미의 조화가 잘 이루어진 운치있는 오름이다. 화구 안쪽 중간부분에서 일부 자연 침식된 스코리아(scoria)층의 단면을 관찰할 수 있다.', 5621, 'A0201', 'A020106', '제주시', '구좌읍', '송당리 2263', 33.447928, 126.777303, 50, 0.0, 0.0, 2585, 0, 0, 2, 11, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:39:56.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025600', 59, 38, 0, '1', NULL, '1', '1', 'JJ', 'Abu Oreum', '164-1 Songdang-ri, Gujwa-eup, Cheju, Jeju-do, South Korea', 'Abu Oreum, sometimes called Ap Oreum, is 300 meters in height and has a gentle slope.', 'The name derives from different origins, but there is a theory that it describes a figure of an adult sitting. It was the filming location of the movie ‘The Uprising’ about the Jeju revolts and features an artificially created cedar tree colony. Although the scale is not particularly large, it is a quaint oreum where natural and artificial beauty are well-harmonized. You can also observe naturally eroded scoria layers in the inner slopes of the middle of the crater. ', 'Jeju City', 'Gujwa Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000041', '수월봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/132978a2-9a25-4865-82d8-1c65d376e1ef.jpg', '수월봉은 천년기념물 제513호이며 볼거리가 많다. ', '우선 2km에 달하는 ''엉알''이라 불리는 해안절벽이 있으며 ''녹고물''이라 불리는 약수터도 있다. 정상에는 기우제를 지낸 수월정이 있고 그 옆으로는 남서해안 기상관측을 하는 고산기상대가 있다. 고산기상대 5층은 전망대로 일반인들에게 개방하고 있다. 이 외에도 초저녁에 수월봉에서 바라보는 일몰 풍경이 아름다워 인기가 많다. 수월봉 아래쪽에는 해안선을 따라 난 지질트레일이 있어 함께 둘러보기에 좋다.', 5621, 'A0201', 'A020102', '제주시', '한경면', '고산리 3763번지일대', 33.2947703, 126.163109, 50, 1.0, 0.0, 1796, 0, 0, 7, 31, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:42:36.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011031000', 46, 35, 0, NULL, NULL, '1', NULL, 'JJ', 'Suwolbong Peak', '3761 Gosan-ri, Hangyeong-myeon, Cheju, Jeju-do, South Korea', 'Suwolbong is the Natural Monument No. 513 with many attractions to see.  ', 'First, there is a coastal cliff called ‘Eongal’ and mineral spring named ‘Nokgomul.’ At the peak is Suwoljeong, where rain calling ceremonies were held, and next to it is an alpine meteorological observatory that surveys the weather in the southwest coast. The 5th floor of the observatory is open to the public. In addition, the view of the sunset from Suwolbong in the early evening is breathtaking. At the bottom of the skirts there is a geological trail along the coastline, a wonderful place to explore together. ', 'Jeju City', 'Hankyung-myeon', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000040', '쇠머리오름(우도봉)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/f3657f6a-2542-44f9-bc89-f917ddad0334.jpg', '우도봉이라고도 하는 쇠머리오름은 우도에 위치하고 있다. ', '우도가 소가 머리를 들고 누워있는 형태를 띠고있는데, 이 머리에 해당하는 부분을 쇠머리오름이라고 하는 것이다. 우도 사람들은 그래서 쇠머리오름을 섬머리라고 한다. 높이 132.5m로 짧은 시간내에 둘러볼 수 있으나 개발로 인해 해가 갈수록 자연적인 모습을 잃고 있다. 응회안(tuff ring)의 수중분화구로 화구 안쪽에 뚜렷하게 난 화구륜을 볼 수 있고, 화구 중앙에는 알오름이라고도 하는 망동산을 볼 수 있다. 이 밖에 파도로 인한 침식작용으로 인해 생긴 동굴과 절벽도 볼 수 있다.', 5621, 'A0201', 'A020108', '제주시', '우도면', '연평리 산19-1번지일대', 33.4916352, 126.9649339, 60, 1.0, 0.0, 820, 0, 0, 15, 48, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-11 17:50:50.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011033000', 60, 38, 0, NULL, NULL, '1', '1', 'JJ', 'Soemeori Oreum (Udobong Peak)', 'Soemeori Oreum, San 18-2 Yeonpyeong-ri, Udo-myeon, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Udo-myeon', NULL, NULL, NULL, NULL, NULL);
