INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000038', '세미소오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/7bb9c27a-22e8-45e6-ad5a-6a38681d0fca.jpg', '나지막한 다섯 봉우리를 지닌 오름으로, 삼나무 숲과 억새 등 풀밭을 이루고 있다. 현재는 천주교의 ''새미 은총의 동산''으로 조성되어 성스러운 장소로 꾸며졌다.', '세미소, 세미동산 외에 세미수오름이나 천미소악 등 여러 별칭으로 불렸던 이유는 오름 정상에 샘이 있었던 데서 유래된 명칭으로 산정화구호가 있었다고 하나, 현재는 원형의 분화구 안에 육상경기장 400m트랙 정도 크기의 인공못이 조성되어 있다. ', 5621, 'A0201', 'A020103', '제주시', '한림읍', '금악리 363번지일대', 33.3499534, 126.3233213, 60, 0.0, 0.0, 923, 0, 0, 0, 1, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:28:31.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025000', 48, 36, 0, NULL, NULL, NULL, '1', 'JJ', 'Saemiso Oreum', '365-3 Geumak-ri, Hallim-eub, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Hallim Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000037', '성불오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/ea35fc0e-3a8e-4f36-bafe-45d22e1ca8e9.jpg', '성불오름이라는 이름은 그 모습이 스님이 염불을 외우는 모습과 비슷하다 해서 붙여진 이름이다.', '남쪽 봉우리에 있는 돌을 목장 방향에서 바라보면 그 모습을 볼 수 있다고 한다. 한편 고려시대 성불암이라는 암자가 있어 그로 인해 붙은 이름이라고도 한다. 길이 나뉘어있지만, 어떤 방향으로 가더라도 결국 이어지도록 구성되어있다. 계절에 따라 달라지는 야생화, 오름을 둘러보며 볼 수 있는 주변경관이 볼만하다.  높이 361.7m로 분화구 골짜기 틈새에 ''성불오름 물''이라 부르는 샘이 있는데 과거에는 물이 풍부하고 식수로 사용했다고하나 현재는 마시기에는 부적합하다.', 5621, 'A0201', 'A020106', '제주시', '구좌읍', '중산간동로 2532', 33.4221237, 126.7499896, 60, 0.0, 0.0, 37, 0, 1, 0, 2, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:28:23.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025600', 59, 38, 0, NULL, NULL, '1', NULL, 'JJ', 'Seongbul Oreum', '266 Songdang-ri, Gujwa-eup, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Gujwa Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000036', '서우봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/5f5b045e-1d65-476e-8c89-4866cfdee71b.jpg', '서우봉은 함덕해수욕장 옆에 위치하고 있으며 높이 113.3m로 매우 완만, 둘레길과 산책로를 갖추고 있다.', '산책로는 약 2.5km로 인근 주민들이 낫과 호미만을 이용해 2년에 걸쳐 조성했다고 한다. 봄에는 유채꽃과 청보리가, 가을에는 황화코스모스가 볼만하다. 역사적으로 보았을 때 삼별초군과 김방경 장군 사이의 대격전이 있었던 장소이며 일제시대때 해군특공대의 기지로 사용한 진지동굴이 있다.', 5621, 'A0201', 'A020105', '제주시', '조천읍', '함덕리 169-1번지일대', 33.54584120000001, 126.67758549999999, 70, 0.0, 0.0, 3173, 0, 0, 15, 16, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:27:28.000', '%Y-%m-%d %H:%i:%s.%f'), '50110', '5011025900', 55, 39, 0, NULL, '1', NULL, '1', 'JJ', 'Seoubong Peak', '169-2 Hamdok-ri, Jochon-eup, Cheju, Jeju-do, South Korea', '', '', 'Jeju City', 'Jocheon-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000035', '새신오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/7339834d-93fd-4e7a-a1e7-77ef87af6484.jpg', '새신오름은 새가 깃들어 사는 산이라는 뜻을 담고있는 오름이다. 지금은 폐교가 된 산양초등학교 인근에 위치한 오름으로 높이는 141m밖에 되지 않는다.', '정상에는 암메라고 부르는 화구가 깊이 약 20m로 넓게 펼쳐져있다. 화구 안쪽으로는 농경지를 비롯해 초지, 묘지가 있다. 일부 한라산을 볼수도 있으나 나무로 인해 전망이 좋지는 않다. 일본군들이 만들어 놓은 진지동굴도 볼 수 있다.  
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000034', '새별오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/991d5d7e-4e80-4b35-a763-c8e4f84a0989.jpg', '새별오름은 밤하늘 외롭게 서있는 샛별과 같다 하여 붙여진 이름이다. ', '매년 정월대보름 즈음에 들불축제가 열리는 장소이기도 한 새별오름은 20년 넘게 제주민들의 무사와 안녕을 기원하는 곳이다. 과거 최영장군이 몽골군과 전투를 치를 당시 격전을 벌인 역사적인 현장이기도 하다. 119m의 높이에 남쪽 봉우리를 중심으로 한 5개의 봉우리를 갖고 있다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000033', '산굼부리', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/1d873a9e-d4b5-4991-8488-336083af7489.jpg', '''굼부리''는 화산의 분화구를 일컫는 제주 말이다. 산굼부리라는 이름은 ''거대한 분화구'' 또는 ''산에 구멍이 뚫린 꼭대기''라는 의미를 담고있다는 설이 있다. ', '산굼부리는 다른 화산들과 비교했을 때 두드러지는 커다란 분화구를 갖고 있으며 천연기념뮬 제 263호로 지정되었다. 화산이 폭발할 당시 용암이 나오지 않고 가스만 분출해 생성된 분화구를 마르(Marr)형이라고 하는데 산굼부리가 이에 해당한다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000032', '사라봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/cc97e129-3189-4f76-b7a4-9bf80dc69f89.jpg', '사라오름이라고도 부르는 사라봉은 제주에서 가장 아름다운 10곳을 선정한 영주십경 중 사봉낙조에 해당한다.', '일몰을 감상하기에 최적의 장소라는 의미다. 지는 해의 모습이 마치 고운 비단과 같다 하여 붙여진 것이 紗羅(사라)다. 바다와 항구 전망이 아름다우며 사라공원내에 별도봉과 함께 둘러볼 수 있다. 산책과 운동을 하는 제주시민들을 쉽게 볼 수 있다. 사라봉 근처에 있는 모충사도 함께 들러볼 수 있다. 
