INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000073', '대병악', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/5606b75c-bc4d-4907-8af8-ec090d17d6ce.jpg', '오름 정상부근이 뭉툭하게 튀어나온 모습이 여자가 머리를 얹은 모습과 비슷하여 ''여진머리오름''이라 불리기도 한다.', '큰병악과 작은병악 두 산이 나란히 서 있어 병악이라 불리며, 쌍둥이 오름이라는 뜻의 골른오름(골오름)이라도고 불린다. 큰오름은 북쪽을 향해 말굽형 화구가 깊이 패어 있고, 작은 오름은 서쪽으로 느슨한 말굽형 화구가 있다. 그 사이에는 알오름이 있으나 형태가 뚜렷하지 않다. ', 5621, 'A0202', 'A020203', '서귀포시', '안덕면', '상창리 산 2-1', 33.3062582, 126.3682575, 70, 0.0, 0.0, 504, 0, 0, 2, 3, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.lch', STR_TO_DATE('2022-07-25 11:48:00.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013031000', 49, 32, 0, NULL, NULL, '1', NULL, 'JJ', 'Daebyeongak Volcanic Cone', '2-2 Sangchang-ri, Andeok-myeon, Seogwipo, Jeju-do, South Korea', '', '', 'Seogwipo City', 'Andeok-myeon', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000072', '대록산', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/c8a94f73-155f-4198-ac91-376abee0a480.jpg', '사슴을 닮은 큰 사슴이오름, 한자로 대록산이라고 표기해 대록산오름이다. 또한 과거에는 이 오름 주변에 사슴이 살았다고 한다.', '제주의 아름다운 길 100선에 꼽힌 녹산로를 따라 이어지는 곳에 위치해 접근성과 안전성이 좋으며, 따라비오름과 함께 가시리권을 대표하는 오름이다. 약 1시간 30분 정도면 정상까지 오를 수 있으며 산책로와 안내판이 잘 정비되어 있어 길을 찾는데 어려움이 없다. 정상을 중심으로 2개의 화구를 갖고 있으며 일제시대 진지동굴도 볼 수 있다. 봄에 유채꽃 잔치가 열리고, 가을에는 억새꽃이 아름답다. ', 5621, 'A0202', 'A020205', '서귀포시', '표선면', '가시리 산 68', 33.3991458, 126.7295447, 70, 0.0, 0.0, 1303, 0, 0, 1, 1, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:41:03.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013032000', 58, 34, 0, NULL, NULL, '1', '1', 'JJ', 'Daeroksan Mountain', 'Noksan-ro, Pyoseon-myeon, Seogwipo-si, Jeju-do, South Korea', 'Mount Daerok is a large oreum that resembles a deer, named Daeroksan using Chinese characters. It is also said that in the past deer lived around this oreum. ', 'It is found along Noksan-ro, listed as one of 100 most beautiful roads in Jeju, thus is accessible and safe. It is a representative oreum in Gasi-ri, along with Ttarabi Oreum. It takes around an hour and a half to it peak, and the trails and signboards are well maintained, so there is no difficulty in self-navigating through the course. It has two craters at the summit, and you can also see the Jinji Cave from the Japanese colonial period. Here, canola flowers in the spring, and pampas grass in the fall creates a splendor. ', 'Seogwipo City', 'a marked surface', NULL, NULL, NULL, NULL, NULL);
