INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000061', '각시바위', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/40c9f7ea-a3be-49ad-a4ca-da544a3e9a48.jpg', '오름 자체가 용암 원정구로 된 바위산이라 산세가 험하지만 북사면으로 향하면 완만한 숲이 있다.', '한 각시가 떨어져 죽은 바위라는데서 각시바위라고 불렸으며 산 체가 마치 학이 날개를 펴고 앉아있는 형상을 닮았다고 하여 학수바위라고도 한다. 북사면은 비교적 완만한 편이나 남사면은 세 가닥의 등성마루가 주봉을 중심으로 뻗은 것이 특징이다. 비교적 자연림이 잘 보호되어 있고 제지기 오름, 섶섬, 문섬, 범섬과 연결되는 제주도 남부해안의 용암원정구대를 이루고 있다.
