INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000096', '수악(물오름)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/ea77031a-40e6-46cb-82e9-920beb3140b8.jpg', '원추형 화산체로 북쪽 도로 건너편에 천연기념물 지정보호되고 있는 ''왕벚나무'' 자생지가 있다.', '5.16도로 공사시 이 곳의 송이를 다량 채취하여 훼손되었으나, 현재는 삼나무 등을 식재하여 조림지로 복귀됐다. 분화구다운 것은 없고 조그만 알오름이 있다. 오름 북쪽에 있는 수악계곡은 선돌계곡과 함께 팔색조의 도래지로 알려져 있다.
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000095', '소병악', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/9328ff35-bab8-4554-89b5-718973ae6a2c.jpg', '말굽형 화구로 이루어진 병악오름의 두 봉우리 중 표고가 낮은 오름을 소병악, 족은오름이라 부른다. ', '큰병악과 작은병악 두 산이 나란히 서 있어 병악이라 불리며, 쌍둥이 오름이라는 뜻의 골른오름(골오름)이라도고 불린다. 큰오름은 북쪽을 향해 말굽형 화구가 깊이 패어 있고, 작은 오름은 서쪽으로 느슨한 말굽형 화구가 있다. 그 사이에는 알오름이 있으나 형태가 뚜렷하지 않다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000094', '성산일출봉', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/2e2ecb10-cc25-495e-b54e-30d3cec57f03.jpg', '제주특별자치도기념물 제 336호, 2007년 7월 2일 UNESCO 세계자연유산에 지정된 성산일출봉. 제주 동쪽 끝에 우뚝 솟아있다.', '제주의 다른 오름들과는 다르게 용암이 물속에서 분출하며 만들어진 수성화산체이다. 덧붙여 성산일출봉은 제주도보다도 먼저 생성되었다. 성산일출봉(城山日出峰)이라는 이름처럼 거대한 성과 같은 이곳에서 바라보는 일출경관이 무척 장엄하고 아름다워 영주 10경(제주의 경승지)중 제1경으로 지정되었다. 서쪽을 제외하고는 절벽이며 서쪽은 잔디로 된 능선의 형태를 띠고 있다. 정상에 있는 깊이 약 1백미터 너비 8만여평의 분화구 주위에 99개의 기암들이 둘러싸고 있다. 만약 봉우리가 100개였다면 맹수가 있었을텐데, 한 개가 모자라 제주도에는 호랑이나 사자가 없다는 전설이 있다. 현재는 볼 수 없지만 과거 성산일출봉 정상에 봉수대가 설치되어 있었다는 기록이 남아있다.', 22484, 'A0202', 'A020206', '서귀포시', '성산읍', '성산리 1', 33.4588827, 126.9408234, 60, 1.0, 1.0, 4288, 1, 0, 410, 711, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:45:05.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013025900', 60, 37, 0, NULL, '1', '1', '1', 'JJ', 'Seongsan Ilchulbong (Sunrise Peak)', 'Seongsan Ilchulbong, 78 Seongsan-ri, Seongsan-eup, Seogwipo, Jeju-do, South Korea', 'As Jeju Special Self-Governing Province Monument No. 336, Seongsan Ilchulbong was designated as UNESCO World Natural Heritage Site on July 2, 2007. It stands tall at the eastern end of Jeju.', 'Unlike other oreums in Jeju, it is an aqueous volcanic body formed by lava erupting in the water. In addition, the Seongsan Ilchulbong was created before Jeju Island.   Like its name, Seongsan Ilchulbong (城山日出峰), the view of the sunrise from here is so majestic and beautiful that it is the first of the Ten Scenic Sights of Yeongju.', 'Seogwipo City', 'Seongsan Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000093', '사려니오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/2dd9b031-95bc-43d8-8f9c-86318f570527.jpg', '주변의 오름들 중에서 표고가 가장 높다. ', '표고 523m, 비고98m로 동쪽 붕우리가 정상이며 가파른 사면에는 삼나무가 울창한 숲을 이루고 있다. 등성마루가 활처럼 휘어있고, 북동쪽으로 넓고 깊게 반달모양의 말굽형 화구가 패여있다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000092', '붉은오름(성산)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/7c7b3de3-1149-42f4-9afa-dea8fb6dffcd.jpg', '유명한 관광명소인 섭지코지가 하나의 오름이며, 오름 일대가 붉은송이와 화산석으로 형성되어 있어 붉은오름이 되었다.', '해수면에 솟아있고 낮아서 구분이 어렵지만 등대를 중심으로 형성된 소화산체이다. 섭지코지는 붉은오름을 포함하는 해안일대이며 등대 부근이 붉은오름의 주봉이다. 제주에서 성산일출봉을 가장 아름답게 볼 수 있는 곳이었으나 건물이 시야를 가리고 있다.', 5621, 'A0202', 'A020206', '서귀포시', '성산읍', '섭지코지로 261', 33.4267249, 126.9305661, 60, 0.0, 0.0, 193, 0, 1, 7, 10, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:40:57.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013025900', 60, 37, 0, NULL, NULL, '1', NULL, 'JJ', 'Bulgeun Oreum (Seongsan)', '89 Goseong-ri, Seongsan-eup, Seogwipo, Jeju-do, South Korea', '', '', 'Seogwipo City', 'Seongsan Township', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000091', '붉은오름(표선)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/e80d2689-7765-463a-8b11-ca5cc6c86862.jpg', '붉은오름은 흙이 붉은 색을 띠고 있어서 라는 설과 과거 고려시대 삼별초와 여몽연합군의 전투때 병사들이 흘린 피때문이라는 설이 있다. ', '붉은오름에 오르는 방법은 크게 2가지가 있다. 붉은자연휴양림으로부터 출발해 성잣성숲길을 이용하는 것, 다른 하나는 말찻오름을 순환하는 해맞이길을 이용하는 것이다. 휴양림 개장 전에는 사려니숲길 입구를 통해 오를 수 있었지만 현재는 불가능하다. 경사가 있어 오르기 쉽지 않지만 붉은자연휴양림과 함께 돌아볼 수 있어 인기가 좋다. 붉은오름휴양림 입구 버스정류장이 있어 대중교통을 이용할 수 있으며, 입구까지 도보로 10분정도 걸린다. 
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000090', '북오름(동광)', '', '굼부리를 중심으로 양쪽으로 북오름과 거린오름이 있으며, 능선이 연결되어 있다.', '표고 314.3m, 비고 84m로 말굽형 화산체이며, 별도의 높은 지점이 없어 전망하는데 좋지 않고 북오름에서 거린오름으로 이어진다.', 5621, 'A0202', 'A020203', '서귀포시', '안덕면', '동광리 산 93', 33.3056468, 126.3313681, 50, 0.0, 0.0, 13, 0, 0, 0, 1, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:39:31.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013031000', 49, 32, 0, '1', NULL, NULL, NULL, 'JJ', 'Buk Oreum (Donggwang)', '93 Donggwang-ri, Andeok-myeon, Seogwipo, Jeju-do, South Korea', '', '', 'Seogwipo City', 'Andeok-myeon', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000089', '병곳오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/9eb44bf4-3406-4209-acb3-b5320d27d27f.jpg', '병곳오름은 명칭과 유래가 여러가지이다. ', '봄에 병 모양의 노란 꽃을 피우는 병화라는 꽃이라는 유래, 남동쪽에 있는 안좌마을과 기러기가 둥지에 앉아있는 모습에서 따 안좌오름이라고도 한다. 번널오름과 이웃하고 있으며 번널오름에 비해 크기가 크다. 화구 안쪽에는 억새가 무성하고 전반적으로 삼나무, 소나무가 주를 이루고 있다. 산정상부에있는 화구는 북동쪽의 골짜기로 이어져있는데, 원래 원형화구였으나 변형되어 말굽형으로 바뀐 것으로 추측하고 있다.
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000165', '족은대비악', '', '''대비''라는 이름의 선녀가 하늘에서 내려와 이 오름에서 놀았다는 이야기가 전해진다.', '족은대비악이니 큰대비악이 있을법도 한데 그런 이름의 오름은 없다. 오름 정상에 두 개의 산정화구를 가지고 있는 복합형 화산체이다. 경사가 심하지 않아 쉽게 오를 수 있고 큰 숲을 이룬 곳이 없어 정상에서 탁 트인 조망권을 확보할 수 있다.', 5621, 'A0202', 'A020203', '서귀포시', '안덕면', '광평리 산59', 33.33045380000001, 126.36904970000005, 60, 0.0, 0.0, 11, 0, 0, 0, 0, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:58.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.ram', STR_TO_DATE('2022-04-06 13:57:23.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013031000', 49, 32, 0, NULL, NULL, '1', NULL, 'JJ', 'Jokeundaebiak Volcanic Cone', '59 Gwangpyeong-ri, Andeok-myeon, Seogwipo, Jeju-do, South Korea', '', '', 'Seogwipo City', 'Andeok-myeon', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000088', '베릿내오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/ffa341c8-3b4b-4d44-a746-de62783c21db.jpg', '베릿내오름은 세 개의 봉우리를 갖고 있고, 은하수처럼 천이 흐른다하여 성천봉(星川峰)이라는 별칭을 갖고 있다.', '중문관광단지에 있는 천제연 폭포 하류쪽 계곡 동쪽 언덕에 위치하고 있으며 제주올레 8코스와 연계하고 있다. 천제연계곡은 난대림을 이루고있는데 식물학적 측면에서 귀중한 가치를 지니고 있으며 환경부에서 지정한 특정야생동식물 중 하나인 솔잎난, 담팔수 등 희귀식물이 자생하고 있어 이 일대를 천연기념물 182-7호로 지정, 보호하고 있다. 서쪽에 있는 주봉인 섯오름은 깊은 절벽을, 동쪽의 오름은 중문동 마을 방향으로 펼쳐지는 얕은 화구를, 만지섬오름은 오름기슭에 만지세미라는 샘을 갖고 있다. ', 5621, 'A0202', 'A020201', '서귀포시', '중문동', '2314-2', 33.2487396, 126.419914, 40, 1.0, 1.0, 759, 0, 0, 9, 12, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:36:52.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013061000', 51, 32, 0, NULL, NULL, '1', NULL, 'JJ', 'Beritne Oreum', '288 Jungmungwangwang-ro, Seogwipo, Jeju-do, South Korea', 'Beritnae Oreum has three peaks and the stream flows like the Milky Way, hence the nickname of the Seongcheonbong (星川峰).', 'Located on the eastern hill of the downstream valley at Cheonjeyeon Falls in the Jungmun Tourist Complex, it is connected to the Jeju Olle Course 8. Cheonjeyeon Valley is a warm-temperate forest that is of invaluable botanical value and is home to rare plants such as Psilotales and Elaeocarpus, which are among the specific wild animals and plants designated by the Ministry of Environment and protected as Natural Monument No. 182-7.', 'Seogwipo City', 'Jungmun-dong', NULL, NULL, NULL, NULL, NULL);
