INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000086', '백약이오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/342713ba-032d-421c-82b2-eba399fe685b.jpg', '이 오름에는 과거 여러가지 약초가 많이 자생했기에 백약이오름(百藥岳)이라고 부른다. ', '현재에도 많지는 않지만 정상에 위치한 분화구 쪽에서 일부 약초를 볼 수 있다. 주변에 오름이 많은데 동거미오름과 문석이오름, 높은오름, 아부오름, 민오름, 비치미오름 등 사방이 오름이다. 전망이 무척 좋아 높이 356.9m를 오른 노고를 보상받는 기분을 느낄 수 있다. ', 5621, 'A0202', 'A020205', '서귀포시', '표선면', '성읍리 산 1', 33.436922000000024, 126.78962069999996, 60, 1.0, 1.0, 1758, 1, 0, 3, 5, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 11:10:44.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013032000', 58, 34, 0, NULL, NULL, NULL, '1', 'JJ', 'Baekyaki Oreum', '1 Seongub-ri, Pyoseon-myeon, Seogwipo, Jeju-do, South Korea', 'This oreum is named Baekyaki (百藥岳) because many medicinal herbs grew wild here in the past. ', 'Though not as many, you can still see herbs growing near the crater at the peak. There are many oreums around, such as Donggeomi, Munseoki, Nopeun, Abu, Min, Bichimi, and so forth. The view is so magnificent that you will feel rewarded for climbing the altitude of 356.9 meters. ', 'Seogwipo City', 'a marked surface', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000085', '민오름(남원)', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/f993dbaa-d315-4ab2-9c36-de2683f95014.jpg', '근처 머체왓숲길과 함께 연계하여 걷기 좋은 오름.', '표고 446.8m 비고 97m로 탐방 목적으로 오르기엔 다소 낮으며, 동쪽으로 말굽형 화구가 트여있다. 과거 민둥산 형태의 오름이라 붙여진 명칭 답지 못하게 숲이 울창하고 둘레를 걸을 수 있는 탐방로가 정비되어 있다. ', 5621, 'A0202', 'A020204', '서귀포시', '남원읍', '수망리 158', 33.35018580000001, 126.67702920000005, 70, 0.0, 0.0, 226, 0, 0, 2, 3, 'S', 'Huple001', STR_TO_DATE('2020-09-16 18:00:57.000', '%Y-%m-%d %H:%i:%s.%f'), 'huple.kyh', STR_TO_DATE('2022-10-12 09:02:20.000', '%Y-%m-%d %H:%i:%s.%f'), '50130', '5013025300', 56, 33, 0, NULL, NULL, NULL, '1', 'JJ', 'Min Oreum (Namwon)', '158, Sumang-ri, Namwon-eup, Seogwipo-si', '', '', 'Seogwipo City', 'Namwon-eup', NULL, NULL, NULL, NULL, NULL);
INSERT INTO place01_tb (place_id, place_nm, rpsnt_image_url, place_outline, place_desc, companion_type_cd, place_loct_cd1, place_loct_cd2, place_sgng_cd, place_umd_cd, place_addr, place_lat, place_lng, lead_time, decision_radius, map_scope, hit_cnt, share_cnt, favorite_cnt, cabinet_cnt, plan_cnt, state, create_id, create_dt, update_id, update_dt, kma_sigungu_code, kma_eubmyundong_code, kma_nx, kma_ny, add_point, spring_post_yn, summer_post_yn, autumn_post_yn, winter_post_yn, city_cd, place_nm_eng, place_addr_eng, place_outline_eng, place_desc_eng, place_sgng_cd_eng, place_umd_cd_eng, inside_yn, outside_yn, public_yn, barrier_free_yn, one_point_yn) VALUES ('PLACE000000000000084', '물영아리오름', 'https://file-upload-bucket.kr.object.ncloudstorage.com/upload-folder/db7ac40d-03cd-450c-8717-451d4a5aac84.jpg', '물영아리오름은 특별하다. 분화구에 물이 고이는 몇 안되는 습지를 갖고 있기 때문이다.', '영아리는 신령스러운 산이라는 의미인데 앞에 ''물''이 붙여 ''물이 있는 신령스러운 산'' 정도로 해석할 수 있다. 이에 얽힌 전설이 있는데, 물영아리 오름의 산신이 노하면 천둥번개가 치거나 폭우가 쏟아진다는 것이다. 자연환경, 생태 등의 중요성을 인정받아 2000년에 습지보호지역으로 지정되었고 2007년에는 람사르습지에 등록되었다. 수망리 중잣성 생태탐방로와 연계되어있다. 잣성은 잣담이라고도 하는데 목초지에 쌓아올린 제주 전통 돌담이다. 
